import { useState } from 'react';
import Table from 'react-bootstrap/Table'
//import Button from 'react-bootstrap/Button'
//import InputGroup from 'react-bootstrap/InputGroup'
import MOCK_DATA from './MOCK_DATA.json'

const TableContent = () => {
  const [searchTerm, setsearchTerm] = useState("");
  return (
    <div className= "app-container" >


      <h2>Cerification Record</h2>

      <div className='container'>
        <input type="text"
         placeholder='Serarch...' 
         style={{marginTop:50, marginBottom: 20, width: "40%"}}
         onChange={(e) =>{
           setsearchTerm(e.target.value)
         }}
         />
      </div>

      {/*Create Table, includes Bootrastap*/}
      <table className='table table-bordered'>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Department</th>
            <th>Position</th>
            <th>Account</th>
            <th>Start Date</th>
            <th>Status</th>
            <th>Course Name</th>
            <th>Training category</th>
            <th>Trainig Date</th>
            <th>End Date</th>
            <th>Training Delivery</th>
            <th>Provider</th>
            <th>Instructor Name</th>
            <th>Training Status</th>
            <th>Certificate Expiration Date</th>
            <th>Comments</th>

          </tr>
        </thead>
        <tbody>

            {MOCK_DATA.filter((val) => {
              if (searchTerm === ""){
                return val;
              } else if (
                val.Name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                val.Email.toLowerCase().includes(searchTerm.toLowerCase())
              ){
                return val;
              }

            }).map((m)=>(
              <tr key={m.id}>
                <td>{m.Name}</td>
                <td>{m.Email}</td>
                <td>{m.Department}</td>
                <td>{m.Position}</td>
                <td>{m.Account}</td>
                <td>{m.Start_Date}</td>
                <td>{m.Status}</td>
                <td>{m.Course_Name}</td>
                <td>{m.Training_Category}</td>
                <td>{m.Trainig_Date}</td>
                <td>{m.End_Date}</td>
                <td>{m.Training_Delivery}</td>
                <td>{m.Provider}</td>
                <td>{m.Instructor_Name}</td>
                <td>{m.Training_Status}</td>
                <td>{m.Certificate_Expiration_Date}</td>
                <td>{m.Comments}</td>

              </tr>
            ))}

        </tbody>
      </table>
    </div>
  )
}

export default TableContent
